mapper.select:
select cm.* from mbbs_board_comment cm
inner join mbbs_board b on b.board_id = cm.board_id
where b.board_id = #{boardId}

mapper.insert:
INSERT INTO public.mbbs_board_comment
(board_id, comment_text, created_by_type, created_by, created_date, updated_by, updated_date)
VALUES(#{boardId}, #{boardComment.comments}, 0, #{boardComment.createdBy}, CURRENT_TIMESTAMP, #{boardComment.updatedBy}, CURRENT_TIMESTAMP);
